package pattern.p007.decorator;

public class DecoratorB implements InterfaceA {
    private InterfaceA a;
    public DecoratorB(InterfaceA a) {
        // TODO Auto-generated constructor stub
        this.a = a;
    }

    @Override
    public void method() {
        // TODO Auto-generated method stub
        System.out.println("msg : before method execute(DecoratorB)");
        this.a.method();
        System.out.println("msg : after method execute(DecoratorB)");
    }

}
